mapper.insert:
INSERT INTO
    emi_comp_info (
                  comp_id
                  , comp_name
                  , corp_reg_num
                  , comp_reg_num
                  , comp_yn
                  , comp_manager
                  , comp_type
                  , comp_sector
                  , comp_cat
                  , comp_zcode
                  , comp_addr1
                  , comp_addr2
                  , comp_addr3
                  , comp_phone
                  , comp_email
                  , comp_fax
                  , reg_dt
                  , reg_user_id
                  , reg_user_ip
)
VALUES (
        #{compId}
        , #{compName}
        , #{corpRegNum}
        , #{compRegNum}
        , #{compYn}
        , #{compManager}
        , #{compType}
        , #{compSector}
        , #{compCat}
        , #{compZcode}
        , #{compAddr1}
        , #{compAddr2}
        , #{compAddr3}
        , #{compPhone}
        , #{compEmail}
        , #{compFax}
        , NOW()
        , #{regUserId}
        , INET_ATON(#{regUserIp})
       )

mapper.update:
UPDATE emi_comp_info SET
    comp_id = #{compId}
    , comp_name =  #{compName}
    , corp_reg_num =  #{corpRegNum}
    , comp_reg_num = #{compRegNum}
    , comp_yn = #{compYn}
    , comp_manager = #{compManager}
    , comp_type = #{compType}
    , comp_sector = #{compSector}
    , comp_cat = #{compCat}
    , comp_zcode = #{compZcode}
    , comp_addr1 = #{compAddr1}
    , comp_addr2 = #{compAddr2}
    , comp_addr3 = #{compAddr3}
    , comp_phone = #{compPhone}
    , comp_email = #{compEmail}
    , comp_fax = #{compFax}
WHERE comp_seq = #{compSeq}
